package instructionselection.mips;import utils.frame.Access;import utils.irtree.BINOP;import utils.irtree.CONST;import utils.irtree.MEM;import utils.irtree.abstractions.Exp;public class InFrame extends Access {    int offset;    InFrame(int o) {	offset = o;    }    public Exp exp(Exp fp) {        return new MEM	    (new BINOP(BINOP.PLUS, fp, new CONST(offset)));    }    public String toString() {        Integer offset = new Integer(this.offset);	return offset.toString();    }}